import React, { useState, useEffect } from "react";
import axios from "axios";
import "./app.scss";

import Select from 'react-select'


function App() {
  const [text, setText] = useState("");
  const [day, setDay] = useState("");  

  const [displayData, setDisplay] = useState([]);


  // useEffect(() => {
  //   fetch("/read")
  //     .then((res) => res.json())
  //     .then((data) => {
  //       console.log(data);
  //     });
  // }, []);

  function sendData(){    
    const data = {id: 1, text: text, day: day };
    console.log(data)
    axios
      .post("http://127.0.0.1:5000/create", data)
      .then((response) => console.log(response));   
  }

  const options = [
    { value: '', label: 'Select...' },
    { value: 'Monday', label: 'Monday' },
    { value: 'Tuesday', label: 'Tuesday' },
    { value: 'Wednesday', label: 'Wednesday' }
  ]

  function getData(day){            
    axios.get("http://127.0.0.1:5000/read/" + day)
    .then(response => setDisplay(response.data.data))    
  }


  return (
    <div className="App">
      <form onSubmit={(e)=>{e.preventDefault()}} autoComplete="off">
        <input 
          value={text} 
          placeholder="To Do"                             
          onChange={(n) => {
            setText(n.target.value);
        }}>

        </input>
        <input 
          value={day} 
          placeholder="Day of the week"
          onChange={(n) => {
            setDay(n.target.value);
        }}>
        </input>
        <div>
          <button
          onClick={()=>{sendData()}}>Send</button>
        </div>
      </form>

      <select 
        value={day} 
        onChange={(e)=>{          
          setDay(e.currentTarget.value)
          getData(e.currentTarget.value)
        }}>
            {options.map((option) => (
              <option key={option.value} value={option.value}>{option.label}</option>
            ))}
        </select>

            <ul> TO DO:
        {displayData.length > 0 &&
          displayData.map((text) => (
              <li key={text} >{text}</li>
            ))

        }
        </ul>

    </div>
  );
}

export default App;
