import React, { useState, useEffect } from "react";
import axios from "axios";

import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

function App() {
  const [text, setText] = useState("");
  const [day, setDay] = useState("");
  const [dayOfWeek, setDayOfWeek] = useState("");


  useEffect(() => {
    fetch("/read")
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
      });
  }, []);

  function sendData(){    
    const data = { text: text, day: day };
    console.log(data)
    axios
      .post("http://127.0.0.1:5000/create", data)
      .then((response) => console.log(response));   
  }

  const options = [
    'Monday', 'Tuesday', 'Wednesday', "Thursday", "Friday"
  ];


  return (
    <div className="App">
      <form onSubmit={(e)=>{e.preventDefault()}} autoComplete="off">
        <input 
          value={text} 
          placeholder="To Do"                             
          onChange={(n) => {
            setText(n.target.value);
        }}>

        </input>
        <input 
          value={day} 
          placeholder="Day of the week"
          onChange={(n) => {
            setDay(n.target.value);
        }}>
        </input>
        <div>
          <button
          onClick={()=>{sendData()}}>Send</button>
        </div>
      </form>

      <Dropdown onChange={setDayOfWeek(dayOfWeek)} options={options} value={dayOfWeek}/>

    </div>
  );
}

export default App;
